UPDATE locales_npc_text SET Text0_0_loc3=NULL, Text0_1_loc3=NULL, Text1_0_loc3=NULL, Text1_1_loc3=NULL, Text2_0_loc3=NULL, Text2_1_loc3=NULL, Text3_0_loc3=NULL, Text3_1_loc3=NULL, Text4_0_loc3=NULL, Text4_1_loc3=NULL, Text5_0_loc3=NULL, Text5_1_loc3=NULL, Text6_0_loc3=NULL, Text6_1_loc3=NULL, Text7_0_loc3=NULL, Text7_1_loc3=NULL;

UPDATE `locales_npc_text` SET `Text0_1_loc3`='Seid gegrüßt, $N' WHERE (`entry`='1');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $N', `Text0_1_loc3`='Seid gegrüßt, $N' WHERE (`entry`='68');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $N', `Text0_1_loc3`='Seid gegrüßt, $N' WHERE (`entry`='384');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='He, $gBürger:Bürgerin ! Ihr seht recht kräftig aus. Wir Wachen sind hier eher spärlich gesät. Daher könnten ich Eure Hilfe gut gebrauchen...' WHERE (`entry`='197');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Viel zu tun, viel zu tun! Der Verband hat uns Fristen gesetzt, die wir besser nicht überschreiten sollten!' WHERE (`entry`='518');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Direktor Riddlevox hat mir die Aufsicht über das Entwicklungsprogramm für Schredder übertragen, das der Verband durchführt. Ich bin erst wenige Monate dabei und sitze schon in der Tinte! Ich könnte Eure Hilfe brauchen, $N!' WHERE (`entry`='519');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$b$bNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen "auszuborgen". Ich habe Wizzlecrank ins Schlickermoor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!' WHERE (`entry`='520');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ihr habt Geistheuler getötet. Ihr seid ein toller Hecht!' WHERE (`entry`='521');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Kommt näher, mein Kind. Zu meiner Zeit konnte ich das flatternde Herz meiner Beute hören, aber heute müsst Ihr laut sprechen, damit Eure Worte gehört werden.' WHERE (`entry`='522');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Viele, viele Jahreszeiten lang zog ich durch diese Ebenen. Und ich habe gewaltige Wildtiere gejagt. Und nie wurde ich übertroffen... bis ich Geistheuler sah.$B$BVor langer Zeit war der enorme Wolf ein Verbündeter der Tauren und kämpfte tapfer gegen die Brennende Legion. Aber er wurde von einem Dämon schlimm verwundet und der Riss in seinem Fleisch ist nie verheilt. Schließlich... trieb er Geistheuler in den Wahnsinn.$B$BJetzt durchstreift er gequält die Ebenen und sucht nach dem Ende seiner Pein. Zu meiner Schande fehlte mir die Kraft, ihn zu töten, als wir kämpften...' WHERE (`entry`='523');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', `Text0_1_loc3`='Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?' WHERE (`entry`='538');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', `Text0_1_loc3`='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE (`entry`='539');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', `Text0_1_loc3`='Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.' WHERE (`entry`='558');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, ich bin ein Magierlehrer und Ihr seid KEIN Magier.' WHERE (`entry`='559');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.', `Text0_1_loc3`='Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.' WHERE (`entry`='560');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir…', `Text0_1_loc3`='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...' WHERE (`entry`='561');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', `Text0_1_loc3`='Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.' WHERE (`entry`='562');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', `Text0_1_loc3`='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE (`entry`='563');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', `Text0_1_loc3`='Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?' WHERE (`entry`='564');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', `Text0_1_loc3`='Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...' WHERE (`entry`='565');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.', `Text0_1_loc3`='Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.' WHERE (`entry`='580');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Mit Zustimmung von Lord Varimathras steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', `Text0_1_loc3`='Mit Zustimmung von Lord Varimathras steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.' WHERE (`entry`='581');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Was wollt Ihr, $C? Ich bin spät dran und mein Arbeitgeber sieht Unpünktlichkeit nicht gern.' WHERE (`entry`='623');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ja bin ich. Was gibts zu tun?' WHERE (`entry`='624');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.', `Text0_1_loc3`='Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.' WHERE (`entry`='638');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Warum besucht ein junger $C wie Ihr einen alten Bauern wie mich?' WHERE (`entry`='678');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Naralex schläft wieder!$b$bVor langer Zeit reiste Naralex mit dem hehren Ziel zu dieser Höhle, in den Smaragdgrünen Traum einzutreten und aus dem Brachland wieder einen üppigen Wald werden zu lassen. Seine Konzentration nahm ab und seine Gedanken wurden von Visionen von Schlangen besudelt.$b$bJetzt sickern reptilienartige Bestien aus seinen Träumen in die Realität. Er muss aus seinem Alptraum erweckt werden, ansonsten wäre er für immer verloren und etwas sehr Böses wäre entfesselt.$b$bErst, wenn seine verderbten Diener, die Giftzahnlords, tot sind, kann ich das Erweckungsritual durchführen.' WHERE (`entry`='698');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Giftzahnlords sind tot! Aber noch ist nicht alle Hoffnung verloren!$b$bDas Erweckungsritual ist jedoch sehr kompliziert. Es kann nicht allein vollzogen werden.$b$blch werde mich um die notwendigen Vorbereitungen kümmern, aber Ihr müsst mich beschützen. Die beschworenen Nachtmahre aus Naralex'' Reise durch den Smaragdgrünen Traum haben sich als äußerst gefährlich erwiesen. Ohne Eure Hilfe ist diese Mission zum Scheitern verurteilt.$b$bWenn Ihr bereit seid, mich zu verteidigen, gebt Bescheid und ich werde Euch den Weg zu Naralex'' Kammer weisen.' WHERE (`entry`='699');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Maschine richtet sich auf Euch aus.$B $BAuf ihrer Rückseite ist ein leerer, seltsam deformierter Schlitz mit einer Inschrift: "Eigntuhm vom Käpt''n Gelkak Gyromast"' WHERE (`entry`='718');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Macht Ihr Euch etwa über mich lustig, während ich an dieses Eisen gefesselt bin und mich nicht verteidigen kann?' WHERE (`entry`='759');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Einer der Menschen hat den Schlüssel zu diesem Höllenapparat. Ich meine, jemand hätte gesagt, er hieße Eston.' WHERE (`entry`='760');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Verschwindet von hier, $C, oder sie werden auch Euch in Ketten legen.' WHERE (`entry`='761');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Marlgen... Einer der Kerkermeister. Er hat den Schlüssel zu meinen Fesseln.' WHERE (`entry`='762');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', `Text0_1_loc3`='Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', `Text1_0_loc3`='Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei und genehmigt Euch einen Schluck.', `Text1_1_loc3`='Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei und genehmigt Euch einen Schluck.' WHERE (`entry`='764');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich wünschte, der Schnee würde einfach wegschmelzen. Er macht meine Robe immer nass und hinterlässt nichts als Dreck!' WHERE (`entry`='766');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Blöder Schnee... He, Ihr seid doch $n, nicht wahr? Ich habe gerade eine Nachricht erhalten, in der nach Euch gefragt wurde.' WHERE (`entry`='767');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ihr wurdet von Lago Finsterzang beschworen, dem Meister aller Hexenmeister von Eisenschmiede. Ihr tätet gut daran, Euch unverzüglich bei ihm zu melden. Ihr findet ihn in der Stadt im Distrikt des düsteren Viertels.' WHERE (`entry`='768');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $R. Mein Name ist Tharnariun Baumwahrer.' WHERE (`entry`='769');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', `Text0_1_loc3`='Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', `Text1_0_loc3`='Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', `Text1_1_loc3`='Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', `Text2_0_loc3`='An Eurer Stelle würde ich einen Goblin-Rennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', `Text2_1_loc3`='An Eurer Stelle würde ich einen Goblin-Rennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', `Text3_0_loc3`='Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', `Text3_1_loc3`='Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', `Text4_0_loc3`='Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', `Text4_1_loc3`='Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.' WHERE (`entry`='778');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Hätten die nicht dieses unglaubliche Goblin-Glück, würden die in jedem Rennen einen Unfall bauen!', `Text0_1_loc3`='Hätten die nicht dieses unglaubliche Goblin-Glück, würden die in jedem Rennen einen Unfall bauen!', `Text1_0_loc3`='In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', `Text1_1_loc3`='In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', `Text2_0_loc3`='Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', `Text2_1_loc3`='Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', `Text3_0_loc3`='Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblin-Schrotthaufen!', `Text3_1_loc3`='Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblin-Schrotthaufen!', `Text4_0_loc3`='Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', `Text4_1_loc3`='Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!' WHERE (`entry`='780');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Endlich! Endlich bin ich aus dieser Zelle befreit!$b$bDie Kirin Tor schickten mich von Dalaran aus, um Arugals Fortschritte mit den Untoten hier im Silberwald zu untersuchen. Wir wussten nicht, dass der Hexer seinen Verstand verloren hatte.$b$bIch muss über diesen Wahnsinn sofort Bericht erstatten! Aber bevor ich gehe, erlaubt mir, das magische Schloss an der Hoftür zu öffnen.$b$bIhr habt Rotkralle besiegt, vielleicht werdet Ihr auch überleben, was jenseits von hier liegt...' WHERE (`entry`='798');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Endlich! Jemand, der mich aus dieser Zelle befreit!$b$bHochexekutor Hadrec schickte uns aus, Informationen über die Burg zu sammeln, sodass ein Plan entwickelt werden konnte, Arugal ein für alle Mal aus dem Weg zu räumen.$b$bAber der alte Hexer hat viele Asse im Ärmel und wir wurden von einem magischen Zauberschutz entdeckt. Ich wurde in den Kerker geworfen. Vincent hatte nicht so viel Glück.$b$bIch muss sofort zurück zu Hadrec zurückkehren und ihm Bericht erstatten. Aber erst werde ich Euch das Schloss der Tür zum Hof öffnen. Vielleicht wollt Ihr Euer Glück gegen die Feinde versuchen, die dahinter lauern.' WHERE (`entry`='799');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Einst folgte ich der Denkart der Schattenhammerkultisten. Ich war besessen davon, die Alten Götter wiederzubeleben, und so drang ich tief in diesen Unterschlupf vor, um mir die Macht der verfluchten Bestie Aku''mai zu Eigen zu machen.$b$bNachdem ich zahllose Unschuldige hatte sterben sehen, kam ich wieder zu Verstand.$b$bIch war gefangen, doch nun bin ich frei. Allerdings bin ich so weit fort von meiner Heimat Khaz Modan!$b$bDaher hört mein Angebot: Ich biete Euch eine Passage in ein ganz besonderes Land als Gegenleistung für den Tod von Aku''mai. Wenn Ihr es wünscht, bringe ich Euch nach Darnassus.' WHERE (`entry`='818');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Willkommen in meinem Gasthaus, müder Reisender. Was kann ich für Euch tun?', `Text0_1_loc3`='Willkommen in meinem Gasthaus, müder Reisender. Was kann ich für Euch tun?' WHERE (`entry`='820');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ruht Eure müden Knochen für einen Zauber aus.', `Text0_1_loc3`='Ruht Eure müden Knochen für einen Zauber aus.' WHERE (`entry`='821');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', `Text0_1_loc3`='Wie der Wind in den Ebenen seid Ihr hier immer willkommen.' WHERE (`entry`='822');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', `Text0_1_loc3`='Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.' WHERE (`entry`='823');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', `Text0_1_loc3`='Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.' WHERE (`entry`='824');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', `Text0_1_loc3`='Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.' WHERE (`entry`='825');
UPDATE `locales_npc_text` SET `Text0_1_loc3`='Hallo, $R, es ist mir ein Vergnügen, Eure Bekanntschaft zu machen.' WHERE (`entry`='838');
UPDATE `locales_npc_text` SET `Text0_1_loc3`='Tja, tja... schaut nur, wer da hereinschneit... und wie kann ich Euch helfen, $C?' WHERE (`entry`='878');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', `Text0_1_loc3`='Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', `Text1_0_loc3`='Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', `Text1_1_loc3`='Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', `Text2_0_loc3`='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', `Text2_1_loc3`='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', `Text3_0_loc3`='Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.\\r\\n\\r\\nIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.', `Text3_1_loc3`='Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.\\r\\n\\r\\nIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.' WHERE (`entry`='879');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', `Text0_1_loc3`='Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', `Text1_0_loc3`='Wollt ''ne Gilde aufmachen, was? Dann viel Glück!\\r\\n\\r\\nSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', `Text1_1_loc3`='Wollt ''ne Gilde aufmachen, was? Dann viel Glück!\\r\\n\\r\\nSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', `Text2_0_loc3`='Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', `Text2_1_loc3`='Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.' WHERE (`entry`='882');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Einen Lehrer für welche Klasse sucht Ihr?', `Text0_1_loc3`='Einen Lehrer für welche Klasse sucht Ihr?' WHERE (`entry`='898');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', `Text0_1_loc3`='Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', `Text1_0_loc3`='Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', `Text1_1_loc3`='Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', `Text2_0_loc3`='Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.', `Text2_1_loc3`='Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.' WHERE (`entry`='899');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', `Text0_1_loc3`='Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', `Text1_0_loc3`='Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', `Text1_1_loc3`='Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', `Text2_0_loc3`='Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.', `Text2_1_loc3`='Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.' WHERE (`entry`='901');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Thorfin Steinschild hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', `Text0_1_loc3`='Thorfin Steinschild hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', `Text1_0_loc3`='Ein Jäger, sagt Ihr? Tja, dann müsst Ihr mit Thorfin Steinschild drüben im Zwergendistrikt reden.\\r\\n\\r\\nAch, und richtet ihm das hier aus: Wenn ich noch einmal in einen der Haufen trete, die sein Tier hinterlassen hat, dann wird er demnächst ganz andere Jäger ausbilden, während er im Verlies hinter Gittern sitzt!', `Text1_1_loc3`='Ein Jäger, sagt Ihr? Tja, dann müsst Ihr mit Thorfin Steinschild drüben im Zwergendistrikt reden.\\r\\n\\r\\nAch, und richtet ihm das hier aus: Wenn ich noch einmal in einen der Haufen trete, die sein Tier hinterlassen hat, dann wird er demnächst ganz andere Jäger ausbilden, während er im Verlies hinter Gittern sitzt!' WHERE (`entry`='905');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. \\r\\n\\r\\nObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', `Text0_1_loc3`='Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. \\r\\n\\r\\nObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', `Text1_0_loc3`='Hexenmeister, hm? Also, Ihr habt das nicht von mir, aber ich hab Gerüchte gehört, dass sich diese Typen im Magierviertel in der Taverne ''Zum geschlachteten Lamm'' treffen.', `Text1_1_loc3`='Hexenmeister, hm? Also, Ihr habt das nicht von mir, aber ich hab Gerüchte gehört, dass sich diese Typen im Magierviertel in der Taverne ''Zum geschlachteten Lamm'' treffen.' WHERE (`entry`='906');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Jeden Tag wird er fetter und korrupter. Man kann den Gestank, den sein Körper absondert, echt schon riechen. Er is'' dreckig un'' damit mein ich nicht nur, dass er sich waschen müsste.$B$BAber genug Plapperei über Hulfdan. Wo kommt Ihr her und wie kann ich Euch helfen, $C?' WHERE (`entry`='908');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Einen Lehrer für welchen Beruf sucht Ihr?', `Text0_1_loc3`='Einen Lehrer für welchen Beruf sucht Ihr?' WHERE (`entry`='918');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wenn Ihr Ausbildung in Alchimie benötigt, gibt es in Sturmwind nur einen Ort dafür... den Alchimiebedarf. Ihr findet ihn im Magierviertel am äußeren Ladenring.', `Text0_1_loc3`='Wenn Ihr Ausbildung in Alchimie benötigt, gibt es in Sturmwind nur einen Ort dafür... den Alchimiebedarf. Ihr findet ihn im Magierviertel am äußeren Ladenring.', `Text1_0_loc3`='Versteckt im Magierviertel liegt ein kleiner Laden namens ''Alchimiebedarf''. Auf Eurem Weg durch das Magierviertel werdet Ihr jede Menge seltsame Läden sehen, aber wenn Ihr am äußeren Rand entlanggeht, dann könnt Ihr ihn nicht verfehlen.', `Text1_1_loc3`='Versteckt im Magierviertel liegt ein kleiner Laden namens ''Alchimiebedarf''. Auf Eurem Weg durch das Magierviertel werdet Ihr jede Menge seltsame Läden sehen, aber wenn Ihr am äußeren Rand entlanggeht, dann könnt Ihr ihn nicht verfehlen.' WHERE (`entry`='919');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Der Zwerg, mit dem Ihr reden solltet, heißt Therum Tiefenschmied. Wenn Ihr mich fragt, ist er einer der besten Schmiede, die es hier gibt. Ihr findet ihn im Herzen des Zwergendistrikts.', `Text0_1_loc3`='Der Zwerg, mit dem Ihr reden solltet, heißt Therum Tiefenschmied. Wenn Ihr mich fragt, ist er einer der besten Schmiede, die es hier gibt. Ihr findet ihn im Herzen des Zwergendistrikts.', `Text1_0_loc3`='Wollt Ihr etwa behaupten, Ihr könntet den Rauch der Zwergenschmieden von hier aus nicht riechen? Wie auch immer, Ihr müsst Therum Tiefenschmied aufsuchen, wenn Ihr die Schmiedekunst erlernen wollt. Er ist drüben im Zwergendistrikt, nördlich vom Kathedralenplatz.', `Text1_1_loc3`='Wollt Ihr etwa behaupten, Ihr könntet den Rauch der Zwergenschmieden von hier aus nicht riechen? Wie auch immer, Ihr müsst Therum Tiefenschmied aufsuchen, wenn Ihr die Schmiedekunst erlernen wollt. Er ist drüben im Zwergendistrikt, nördlich vom Kathedralenplatz.' WHERE (`entry`='920');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wenn Ihr ein phantastischer Koch werden wollt, geht zur Taverne Zum pfeifenden Schwein und sprecht mit Stephen Ryback. Der Mann kann wie kein anderer eine ganze Ladung Rippchen zubereiten.\\r\\n\\r\\nHimmel... jetzt habt Ihr mir Appetit auf seine berühmten Rippchen gemacht. Aber rechnet nicht damit, dass er Euch das Rezept dafür verrät.', `Text0_1_loc3`='Wenn Ihr ein phantastischer Koch werden wollt, geht zur Taverne Zum pfeifenden Schwein und sprecht mit Stephen Ryback. Der Mann kann wie kein anderer eine ganze Ladung Rippchen zubereiten.\\r\\n\\r\\nHimmel... jetzt habt Ihr mir Appetit auf seine berühmten Rippchen gemacht. Aber rechnet nicht damit, dass er Euch das Rezept dafür verrät.', `Text1_0_loc3`='In der Taverne Zum pfeifenden Schwein in der Altstadt arbeitet ein genialer Koch. Ich habe mal gesehen, wie er das Messer schwingt, wenn er kocht, und - unter uns gesagt - ich glaube nicht, dass er sein Leben lang ein Koch war. Versteht mich nicht falsch - wenn Ihr immer noch lernen möchtet, ist er der beste Lehrer weit und breit.', `Text1_1_loc3`='In der Taverne Zum pfeifenden Schwein in der Altstadt arbeitet ein genialer Koch. Ich habe mal gesehen, wie er das Messer schwingt, wenn er kocht, und - unter uns gesagt - ich glaube nicht, dass er sein Leben lang ein Koch war. Versteht mich nicht falsch - wenn Ihr immer noch lernen möchtet, ist er der beste Lehrer weit und breit.' WHERE (`entry`='921');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Der in Sturmwind wohnende Ingenieurskunstlehrer ist ein geschickter Gnom namens Liliam Spindelfunks. Er ist im hinteren Teil des Zwergendistrikts zu finden.', `Text0_1_loc3`='Der in Sturmwind wohnende Ingenieurskunstlehrer ist ein geschickter Gnom namens Liliam Spindelfunks. Er ist im hinteren Teil des Zwergendistrikts zu finden.', `Text1_0_loc3`='He, da fällt mir ein: Ich muss meine Raketenstiefel noch reparieren lassen. Kleiner Tipp am Rande, $gmein Freund:meine Liebe;: Raketenstiefel und Greife passen nicht zusammen. \\r\\n\\r\\nAch ja, Ihr braucht noch eine Wegbeschreibung... geht einfach rüber in den Zwergendistrikt - Lilliam Spindelfunks findet Ihr in dessen hinterem Teil.', `Text1_1_loc3`='He, da fällt mir ein: Ich muss meine Raketenstiefel noch reparieren lassen. Kleiner Tipp am Rande, $gmein Freund:meine Liebe;: Raketenstiefel und Greife passen nicht zusammen. \\r\\n\\r\\nAch ja, Ihr braucht noch eine Wegbeschreibung... geht einfach rüber in den Zwergendistrikt - Lilliam Spindelfunks findet Ihr in dessen hinterem Teil.' WHERE (`entry`='922');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Kathedrale des Lichts ist Sturmwinds Zentrum aller Heilpraktiken. Ihr findet dort auch Shaina Fuller, von der Ihr diese Kunst erlernen könnt.', `Text0_1_loc3`='Die Kathedrale des Lichts ist Sturmwinds Zentrum aller Heilpraktiken. Ihr findet dort auch Shaina Fuller, von der Ihr diese Kunst erlernen könnt.', `Text1_0_loc3`='Die Erste Hilfe erlernt Ihr am besten in der Kathedrale des Lichts, unter den wachsamen Augen von Shaina Fuller. \\r\\n\\r\\nUm dorthin zu gelangen, müsst Ihr entweder nach den gelben Dächern Ausschau halten oder nach der Turmspitze der Kathedrale selbst, denn die kann man von fast überall in der Stadt aus sehen.', `Text1_1_loc3`='Die Erste Hilfe erlernt Ihr am besten in der Kathedrale des Lichts, unter den wachsamen Augen von Shaina Fuller. \\r\\n\\r\\nUm dorthin zu gelangen, müsst Ihr entweder nach den gelben Dächern Ausschau halten oder nach der Turmspitze der Kathedrale selbst, denn die kann man von fast überall in der Stadt aus sehen.' WHERE (`entry`='923');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ihr könnt Tannysa im Magierviertel vor dem Alchimiebedarf finden.', `Text0_1_loc3`='Ihr könnt Tannysa im Magierviertel vor dem Alchimiebedarf finden.', `Text1_0_loc3`='Es geht doch nichts darüber, mit der Natur Zwiesprache zu halten... das habe ich mir zumindest von Nachtelfen sagen lassen. Ich selber hab''s ja nicht so damit, mit Blumen und Bäumen zu reden, aber ich bin mir sicher, Tannysa kann Euch helfen. Sie ist drüben im Magierviertel, vor dem ''Alchimiebedarf''.', `Text1_1_loc3`='Es geht doch nichts darüber, mit der Natur Zwiesprache zu halten... das habe ich mir zumindest von Nachtelfen sagen lassen. Ich selber hab''s ja nicht so damit, mit Blumen und Bäumen zu reden, aber ich bin mir sicher, Tannysa kann Euch helfen. Sie ist drüben im Magierviertel, vor dem ''Alchimiebedarf''.' WHERE (`entry`='924');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wollt die Lederverarbeitung lernen, ja? Simon Gerber sollte es Euch beibringen können. Ihr findet ihn im Schützenden Balg in der Altstadt.', `Text0_1_loc3`='Wollt die Lederverarbeitung lernen, ja? Simon Gerber sollte es Euch beibringen können. Ihr findet ihn im Schützenden Balg in der Altstadt.', `Text1_0_loc3`='Ihr interessiert Euch für Leder, was? Klingt, als solltet Ihr mal mit Simon Gerber drüben in der Altstadt reden. Er führt dort einen Laden namens ''Der schützende Balg''.', `Text1_1_loc3`='Ihr interessiert Euch für Leder, was? Klingt, als solltet Ihr mal mit Simon Gerber drüben in der Altstadt reden. Er führt dort einen Laden namens ''Der schützende Balg''.' WHERE (`entry`='925');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Gelman Steinhand ist derjenige, mit dem Ihr reden solltet. Ich glaube, dass er drüben im Zwergendistrikt ein Haus hat, aber kann mich nicht genau erinnern. Ich würde jedenfalls dort nachschauen.', `Text0_1_loc3`='Gelman Steinhand ist derjenige, mit dem Ihr reden solltet. Ich glaube, dass er drüben im Zwergendistrikt ein Haus hat, aber kann mich nicht genau erinnern. Ich würde jedenfalls dort nachschauen.', `Text1_0_loc3`='Typisch Zwerge: Schicken extra einen Minenspezialisten aus Eisenschmiede, um uns beizubringen, wie man Bergbau betreibt. Wie schwer kann es schon sein, eine Hacke in die Hand zu nehmen und auf ein paar Felsen einzuhauen? Na, jedenfalls ist er hier, also solltet Ihr Euch wohl auch mit ihm unterhalten. Er heißt Gelman und wohnt drüben im Zwergendistrikt.', `Text1_1_loc3`='Typisch Zwerge: Schicken extra einen Minenspezialisten aus Eisenschmiede, um uns beizubringen, wie man Bergbau betreibt. Wie schwer kann es schon sein, eine Hacke in die Hand zu nehmen und auf ein paar Felsen einzuhauen? Na, jedenfalls ist er hier, also solltet Ihr Euch wohl auch mit ihm unterhalten. Er heißt Gelman und wohnt drüben im Zwergendistrikt.' WHERE (`entry`='927');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wenn Ihr die Kürschnerei lernen wollt, solltet Ihr rüber in die Altstadt gehen und im Schützenden Balg reinschauen.', `Text0_1_loc3`='Wenn Ihr die Kürschnerei lernen wollt, solltet Ihr rüber in die Altstadt gehen und im Schützenden Balg reinschauen.', `Text1_0_loc3`='Wisst Ihr, ich könnte einen Orc aufspießen oder einen Troll in Einzelteile zerlegen; ich könnte sogar meine Anfangsbuchstaben in einen Oger ritzen, aber beim Gedanken daran, ein Tier zu häuten, wird mir schlecht. Komisch, was? \\r\\n\\r\\nNa was soll''s, wenn Euch das nichts ausmacht, dann sucht Maris Granger in ''Der schützende Balg'' in der Altstadt auf.', `Text1_1_loc3`='Wisst Ihr, ich könnte einen Orc aufspießen oder einen Troll in Einzelteile zerlegen; ich könnte sogar meine Anfangsbuchstaben in einen Oger ritzen, aber beim Gedanken daran, ein Tier zu häuten, wird mir schlecht. Komisch, was? \\r\\n\\r\\nNa was soll''s, wenn Euch das nichts ausmacht, dann sucht Maris Granger in ''Der schützende Balg'' in der Altstadt auf.' WHERE (`entry`='928');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Duncans Textilien beschäftigt einen der besten Schneider Sturmwinds namens Georgio Bolero. Der Laden befindet sich drüben im Magierviertel.', `Text0_1_loc3`='Duncans Textilien beschäftigt einen der besten Schneider Sturmwinds namens Georgio Bolero. Der Laden befindet sich drüben im Magierviertel.', `Text1_0_loc3`='Ein Schneider, sagt Ihr? Na, wenn Ihr Euch lange genug hier herumtreibt, dann werdet Ihr schon früh genug von Georgio Bolero hören. Er bestimmt jetzt schon seit ein paar Monaten die Mode hier. Wenn Ihr von ihm lernen möchtet, dann findet Ihr ihn drüben im Magierviertel am äußeren Rand.', `Text1_1_loc3`='Ein Schneider, sagt Ihr? Na, wenn Ihr Euch lange genug hier herumtreibt, dann werdet Ihr schon früh genug von Georgio Bolero hören. Er bestimmt jetzt schon seit ein paar Monaten die Mode hier. Wenn Ihr von ihm lernen möchtet, dann findet Ihr ihn drüben im Magierviertel am äußeren Rand.' WHERE (`entry`='929');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich hoffe, Ihr seid für die Arbeit bereit.' WHERE (`entry`='931');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Willkommen bei den Rennen! Genießt Euren Aufenthalt und verwettet nicht Euer ganzes Geld auf einmal!', `Text0_1_loc3`='Willkommen bei den Rennen! Genießt Euren Aufenthalt und verwettet nicht Euer ganzes Geld auf einmal!', `Text1_0_loc3`='Seid gegrüßt, $C! Willkommen bei den Rennen der schimmernden Ebene!', `Text1_1_loc3`='Seid gegrüßt, $C! Willkommen bei den Rennen der schimmernden Ebene!', `Text2_0_loc3`='Wer hat den schnellsten Wagen? Wer weiß?!?', `Text2_1_loc3`='Wer hat den schnellsten Wagen? Wer weiß?!?', `Text3_0_loc3`='Der Goblin-Rennwagen verträgt zwar eine Menge, aber die Gnome haben in ihren Wagen ein oder zwei Tricks eingebaut...', `Text3_1_loc3`='Der Goblin-Rennwagen verträgt zwar eine Menge, aber die Gnome haben in ihren Wagen ein oder zwei Tricks eingebaut...' WHERE (`entry`='932');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wofür braucht Ihr Anweisungen?', `Text0_1_loc3`='Wofür braucht Ihr Anweisungen?' WHERE (`entry`='933');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wie eine nasse Katze, die man aus dem Regen holt, kratzt und klammert Ihr, und dann macht Ihr es doch wieder... Ich mache was wieder...?$B$BOh, ich habe Euch nicht hereinkommen sehen, $N! Was kann ich für Euch tun?' WHERE (`entry`='934');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Wenn wir diese neuen Ländereien zu den unseren machen wollen, müssen wir Orte haben, an denen wir unsere vom Kämpfen geschwächten Körper ausruhen können. Legt Eure Axt ans Feuer und lasst uns Geschichten Eurer ruhmreichen Schlachten hören.', `Text0_1_loc3`='Wenn wir diese neuen Ländereien zu den unseren machen wollen, müssen wir Orte haben, an denen wir unsere vom Kämpfen geschwächten Körper ausruhen können. Legt Eure Axt ans Feuer und lasst uns Geschichten Eurer ruhmreichen Schlachten hören.' WHERE (`entry`='938');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Lucan Cordell besitzt einen kleinen Laden am Kanal beim Verlies. Ich bin sicher, dass er Euch die Verzauberkunst beibringen kann.', `Text0_1_loc3`='Lucan Cordell besitzt einen kleinen Laden am Kanal beim Verlies. Ich bin sicher, dass er Euch die Verzauberkunst beibringen kann.', `Text1_0_loc3`='Ah, Ihr sucht Lucan Cordell. Er führt einen Laden außerhalb des Magierviertels, zwischen hier und dem Verlies.', `Text1_1_loc3`='Ah, Ihr sucht Lucan Cordell. Er führt einen Laden außerhalb des Magierviertels, zwischen hier und dem Verlies.' WHERE (`entry`='941');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich reise allein, um meine Ehre zu verteidigen und dieses Land von unseren Feinden zu befreien.$B$BWas kann ich für Euch tun, $N?' WHERE (`entry`='942');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Den Orden der Zerschmetterten Hand gab es schon vor Thrall und es wird ihn auch nach Thrall noch geben.' WHERE (`entry`='1038');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Zando''zan von der Zerschmetterten Hand. Wie kann Zando''zan Euch helfen? Gar nicht! Und er wird es auch nicht!' WHERE (`entry`='1039');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid Ihr zur Ausbildung hier? Oder seid Ihr nur hier, um die Zeit eines Kriegers zu verschwenden?', `Text0_1_loc3`='Seid Ihr zur Ausbildung hier? Oder seid Ihr nur hier, um die Zeit eines Kriegers zu verschwenden?' WHERE (`entry`='1040');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Maschine springt bei Eurer Berührung an.' WHERE (`entry`='1041');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Das ist eine exotische Pflanzenart, die man nirgendwo sonst in Teldrassil findet.' WHERE (`entry`='1059');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Diese Pflanze hebt sich von allen gewöhnlichen Pflanzen Teldrassils ab und verbreitet einen seltsamen, lilafarbenen Schein.' WHERE (`entry`='1060');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Willkommen. Meine Aufgabe besteht darin, Euch Zugang und, falls erlaubt, auch Einblick in das Werk der Schöpfer zu gewähren, die für die Welt Azeroth die Irdenen geschaffen haben. Ich kann Euch diese Dinge nun anvertrauen, wenn Ihr bereit seid.' WHERE (`entry`='1079');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Irdenen sind ein von den Schöpfern künstlich geschaffenes Volk. Sie fungierten in der Welt Azeroth ursprünglich einmal als eines der Stammvölker. Die Irdenen dienten den Schöpfern als Helfer bei der Formung der Tiefen dieser Welt.$B$BSie stellen eine Abwandlung der Standardmatrix für unterirdische Lebewesen dar, wie sie die Schöpfer auf anderen Welten benutzt haben. Bei der Konstruktion dieses Prototyps entstanden diverse Anomalien, die allerdings erst später bemerkt wurden.' WHERE (`entry`='1080');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Es handelt sich um eine der Synthesematrizen, die die Schöpfer beim Anlegen einer Welt verwenden. Jede Synthesematrix dient dabei einem Ziel der Schöpfer. Für Azeroth wurde die Matrix für unterirdische Lebewesen so modifiziert, dass ein ausdauerndes Wesen mit einer Vorliebe für die Formung tiefer Regionen entstand: Die Irdenen sind das Ergebnis dieser Modifkation.$B$BNachdem die Irdenen den Dienst für die Schöpfer aufgenommen hatten, kam es zu unvorhergesehenen Problemen, wenn die Synthesematrix unter hohen Stressdruck geriet.' WHERE (`entry`='1081');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Um diese Frage korrekt zu beantworten, müsst Ihr noch einiges zum Hintergrund der Irdenen wissen.$B$BBerechnung läuft.$B$BDie Anomalien der Irdenen erklären sich aus dem Zweck, zu dem sie geschaffen wurden. Die Irdenen sind ein Versuch der Schöpfer, die Standardmatrix für unterirdische Lebewesen überlebensfähiger zu machen. Zu diesem Zweck wurde besagter Matrix ureigenstes widerstandsfähiges Material aus der Welt hinzugefügt.' WHERE (`entry`='1082');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Es ist das Grundmaterial für die Schaffung ganzer Völker. Für die Schaffung der Irdenen wurde statt der üblichen Biomasse ein Querschnitt der Erdoberfläche Azeroths verwendet.$B$BEine Analyse der Zusammensetzung der Welt hatte die Schöpfer zu der Theorie geführt, dass sie daraus ein intensiviertes Lebewesen erschaffen könnten, das die Widerstandsfähigkeit der Essenz dieser Welt hat. Dies erreichten sie durch eine Mischung des Gesteinsmaterials von Azeroth als Grundlage.' WHERE (`entry`='1083');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Vereinfacht ausgedrückt trifft dies zu.$B$BRein äußerlich betrachtet gleichen die Irdenen klein gewachsenen Humanoiden, aber sie bestehen aus den diversen Komponenten des Gesteinsmaterials Azeroths. Dadurch wurden Kraft und Ausdauer maximiert, ohne dass dies zu Lasten ihrer kognitiven Fähigkeiten ging. Haut und Muskulatur sind praktisch nicht zu beschädigen, und mit nur sehr schwachen Modifikationen zeigen die Irdenen erstaunliche Widerstandsfähigkeit gegen Magie.' WHERE (`entry`='1084');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Hierzu muss man unbedingt wissen, dass die Schöpfer ein Volk schaffen wollten, das lange nach ihrer Abreise noch integraler Bestandteil des Entwicklungszyklus'' der Welt sein würde. Dieses Volk sollte die langfristigen Entwicklungsziele für Azeroth erfüllen. $B$BDer Name "die Irdenen" schien den Schöpfern angesichts der Zusammensetzung des neuen Volks im Übrigen nur passend.' WHERE (`entry`='1085');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Das einzige Problem bei den Irdenen besteht in ihrer tendenziellen Instabilität unter hohem Stressdruck.$B$BAddendum: Die neue Matrix war speziell für die Formung tiefer Erdschichten geschaffen worden und erfüllte folglich niemals Anforderungen zur Duplizierung einer Hochstressdruckumgebung.$B$BDoch diese Destabilisierung wurde erst lange nach Beginn des ersten Dienstleistungszyklus'' für die Schöpfer festgestellt. Die Schöpfer nahmen nachträgliche Änderungen vor, statt noch einmal von vorn zu beginnen.' WHERE (`entry`='1086');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='[Nach der Frage folgt eine merkliche Pause.]$B$BDie gewünschte Auskunft ist momentan in diesem Datenspeicher nicht verfügbar. Dies bedaure ich zutiefst.$B$BBitte richtet die Anfrage erneut an mich, wenn der Informationsdatenspeicher aktualisiert worden ist. Oder fragt die gewünschte Auskunft von einem anderen Datenspeicher in Azeroth ab.' WHERE (`entry`='1087');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Es gibt zwei mögliche Ergebnisse, wenn sich die Synthesematrix der Irdenen destabilisiert. Beginne mit optischer Hilfsdarstellung eins.$B$BDie erste Matrixvariante ist ein degeneratives Syntheseprodukt, für gewöhnlich als "Trogg" bezeichnet. Diese Variante ist ähnlich stark und ausdauernd wie die Irdenen, die kognitiven Fähigkeiten sind jedoch fast vollständig ausgelöscht. Triebfedern eines Troggs sind einzig seine starke Willenskraft und Schlauheit.' WHERE (`entry`='1088');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Zu meinem Bedauern bin ich nicht in der Lage, Euren Zeitbezug mit den hier aufgezeichneten Daten abzugleichen.$B$BDer Trogg enthält verschiedenes Steinmaterial von Azeroth, aber die fehlenden kognitiven Fähigkeiten machen diese Abweichung für die Schöpfer inakzeptabel.$B$BEin Trogg vermehrt sich so wie eine Standardbiomasse. In der Regel frisst er Steine und alles andere, auch andere Troggs. Kannibalismus ist für die Schöpfer jedoch inakzeptabel.' WHERE (`entry`='1089');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Beginne mit optischer Hilfsdarstellung zwei.$B$BDie zweite resultierende Abweichung von der Matrix für die Irdenen weist im Vergleich zur Standardmatrix der unterirdischen Lebewesen viele erwünschte Elemente auf. In der Regel wird diese Form als "Zwerg" bezeichnet.$B$BDiese Form weist Stärke und Ausdauer der Irdenen auf. In einigen Fällen übersteigen die kognitiven Fähigkeiten der Zwerge sogar die der eigentlichen Matrix. Allerdings enthalten die Zwerge nicht mehr das ursprüngliche Steinmaterial aus dem Inneren Azeroths.' WHERE (`entry`='1090');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die hier gespeicherten Daten korrelieren nicht mit Eurer klugen Anmerkung.$B$BZwerge weisen nach wie vor eine Vorliebe für die steinerne Zusammensetzung Azeroths auf, doch aufgrund der unter hohem Druck auftretenden Anomalien in der Synthesematrix für die Irdenen sind Zwerge nicht mehr als Standardbiomasse.$B$BFolglich weisen Zwerge auch nicht die Degenerierungen der Schöpfungsmatrix der Irdenen auf, wie Ihr mit Eurer Frage vielleicht andeuten wolltet.' WHERE (`entry`='1091');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Zu meinem Bedauern bin ich nicht in der Lage, Euren Zeitbezug mit den hier aufgezeichneten Daten abzugleichen.$B$BZwar weisen Zwerge größtenteils die geistige Verbundenheit der Irdenen mit dem Steinmaterial aus den Tiefen Azeroths auf, aber ihre Rückentwicklung in lebende Biomasse stellt die Hauptdegeneration der Matrix dar. Zwerge essen und vermehren sich auf für Standardbiomasse typische Weise.$B$BDie Schöpfer sehen die Zwerge als akzeptable Variante an, die einer eigenen Schöpfungsgeschichte würdig ist.' WHERE (`entry`='1092');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Schöpfer sind... die Schöpfer.$B$BSie sind die Wesen, die diese Welt angelegt und geformt haben. Sie sind der Grund für das Dasein dieser und vieler anderer Welten. Sie sind der Quell, aus dem Azeroth entspringt.' WHERE (`entry`='1093');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Schöpfer sind sich der Fülle der in diesem Speicher enthaltenen Angaben bewusst. Folglich wird Euch eine tragbare Ausgabe der hier gespeicherten Daten zur Verfügung gestellt, sobald Ihr Euch mit den Scheiben von Norgannon vertraut gemacht habt.$B$BBitte betrachtet Euren oberflächlichen Wissensdurst hiermit als gestillt. Greift bei Bedarf auf die Scheiben von Norgannon zu, um mit der Synthese Eurer Scheiben zu beginnen.' WHERE (`entry`='1094');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Schmied wollt Ihr also werden? Dann schnappt Euch mal Hammer und Schürze, $gBursche:Mädchen;, und lasst mich sehen, was Ihr draufhabt.' WHERE (`entry`='1118');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die Zerschmetterte Hand beherrscht diesen Landstrich. Wir sind der Schleier, der über Kalimdor liegt: Wir sind Beschützer, Ordnungsmacht, Richter, Geschworene und Henker in einem. Wir sehen alles und wir wissen alles.$B$BIhr habt Euch gut weiterentwickelt, seit Ihr der Hand dient, $N. Es wird Zeit für Euren ersten echten Einsatz.$B$BSeid Ihr bereit für die nächste Mission?' WHERE (`entry`='1124');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Willkommen in der großen Schmiede, $gJunge:Mädchen;. Draußen mag es kalt sein, aber wir hier drinnen haben''s immer gemütlich warm. Schnappt Euch einen Hammer, bearbeitet den Amboss und zeigt mir, ob Ihr ein Schmied der großen Schmiede sein könnt!' WHERE (`entry`='1127');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Nette Form, $N. Ich wette um einen fetten Krug in Bruuks Ecke, dass die Funktion auch nichts zu wünschen übrig lässt. Wisst Ihr was? Ihr solltet vielleicht ma'' mit Myolor Zornesbeben reden. Der wird''s Euch schon zeigen.' WHERE (`entry`='1128');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Klar kann ich Euch sagen, wo Myolor is''. Gleich da drüben in der Nähe vom Amboss der großen Schmiede, da macht er seine Arbeit. Hört ihm gut zu, $GBursche:Mädchen;, Myolor ist der Beste von allen.' WHERE (`entry`='1129');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Meinen Segen habt Ihr, $N. Ihr könnt bessere Klingen schmieden als alle, die ich in den letzten Jahren ausgebildet habe. Wenn Ihr aber mal ''ne echte Waffe schmieden wollt, dann haltet Euch an Myolor Zornesbeben. Das is''n Zwerg, der Euch auf''n richtigen Weg bringt.' WHERE (`entry`='1130');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Aha, Ihr wollt also zu Myolor, was? Der is'' grad da drüben... hinter Euch! Nee, vielleicht auch vor Euch! Beim groß''n Amboss halt.' WHERE (`entry`='1131');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid Ihr komplett meschugge? Das ist keine Waffe, das ist ein Frühstücksmesser. Ich gebe mich nicht mit Anfängern ab.' WHERE (`entry`='1133');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Dafür habt Ihr mich gestört? Da seid Ihr ja besser dran, wenn Ihr Euch einen toten Hasen auf den Rücken schnallt und damit durch eine Trollhöhle rennt!' WHERE (`entry`='1135');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='$N, richtig? Das ist mächtig gute Arbeit, die Ihr da abgeliefert habt. Ich nehme mich Eurer an und zeig Euch, wie Ihr ''ne Rüstung macht, die König Magni höchstselbst gut zu Gesicht stehen würde.' WHERE (`entry`='1136');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.', `Text0_1_loc3`='Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.' WHERE (`entry`='1202');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.', `Text0_1_loc3`='Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.' WHERE (`entry`='1203');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Willkommen in Sturmwind. Hat lange gedauert, die Stadt wieder aufzubauen, aber das war es wert. Seid Ihr nur zu Besuch oder geschäftlich hier?', `Text0_1_loc3`='Willkommen in Sturmwind. Hat lange gedauert, die Stadt wieder aufzubauen, aber das war es wert. Seid Ihr nur zu Besuch oder geschäftlich hier?' WHERE (`entry`='1206');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Bei mir schauen nicht oft andere Köche wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.', `Text0_1_loc3`='Bei mir schauen nicht oft andere Köche wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.' WHERE (`entry`='1207');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Sehe ich vielleicht aus, als hätte ich die Zeit, jeden Touristen zu verhätscheln, der seinen Kopf durch diese Tür steckt? Sagt mir, was Ihr wollt, und fasst Euch kurz!', `Text0_1_loc3`='Sehe ich vielleicht aus, als hätte ich die Zeit, jeden Touristen zu verhätscheln, der seinen Kopf durch diese Tür steckt? Sagt mir, was Ihr wollt, und fasst Euch kurz!' WHERE (`entry`='1208');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Bei mir schauen nicht oft andere Kürschner wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.', `Text0_1_loc3`='Bei mir schauen nicht oft andere Kürschner wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.' WHERE (`entry`='1210');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die großen Kriege mögen ja für den Augenblick vorbei sein, aber wir dürfen dennoch nicht nachlassen!', `Text0_1_loc3`='Die großen Kriege mögen ja für den Augenblick vorbei sein, aber wir dürfen dennoch nicht nachlassen!' WHERE (`entry`='1215');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Sturmwind ist die Säule der Menschheit, aufrecht gehalten von Willenskraft und der Schneide einer stabilen Klinge.', `Text0_1_loc3`='Sturmwind ist die Säule der Menschheit, aufrecht gehalten von Willenskraft und der Schneide einer stabilen Klinge.' WHERE (`entry`='1216');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt. Ihr sucht Anleitung in der Kriegskunst?', `Text0_1_loc3`='Seid gegrüßt. Sucht Ihr Anleitung in der Kriegskunst?' WHERE (`entry`='1217');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ganz gleich wie viel man zu wissen glaubt, man lernt nie aus.', `Text0_1_loc3`='Ganz gleich wie viel man zu wissen glaubt, man lernt nie aus.' WHERE (`entry`='1218');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Oh, ein Arzt, wie ich einer bin! Es wäre mir eine Freude, Euch alles zu lehren, was Euch auf Euren Reisen nützlich sein kann.', `Text0_1_loc3`='Oh, ein Arzt, wie ich einer bin! Es wäre mir eine Freude, Euch alles zu lehren, was Euch auf Euren Reisen nützlich sein kann.' WHERE (`entry`='1220');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ja, $gmein Sohn:meine Tochter;? Was kann ich für Euch tun?', `Text0_1_loc3`='Ja, $gmein Sohn:meine Tochter;? Was kann ich für Euch tun?' WHERE (`entry`='1221');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich kam von Eisenschmiede hierher, um beim Wiederaufbau zu helfen. Wenn ich Euch genug von meinen Fertigkeiten lehre, kann ich diese Aufgabe vielleicht in Eure Hände legen und nach Hause zurückkehren.', `Text0_1_loc3`='Ich kam von Eisenschmiede hierher, um beim Wiederaufbau zu helfen. Wenn ich Euch genug von meinen Fertigkeiten lehre, kann ich diese Aufgabe vielleicht in Eure Hände legen und nach Hause zurückkehren.' WHERE (`entry`='1225');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Was ist? Ihr stört, und dafür solltet Ihr einen verflucht guten Grund haben!', `Text0_1_loc3`='Was ist? Ihr stört, und dafür solltet Ihr einen verflucht guten Grund haben!' WHERE (`entry`='1226');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seid gegrüßt, $N.' WHERE (`entry`='1228');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text0_1_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text1_0_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', `Text1_1_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE (`entry`='1231');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text0_1_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text1_0_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', `Text1_1_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE (`entry`='1232');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Geschaffen von den besten Handwerkern in ganz Teldrassil und als Zeichen der Festigkeit der Allianz zwischen dem Volk des großen Baums und den Menschen dieses Landes durch Boten von Kalimdor nach Azeroth gebracht. Wenn Ihr etwas braucht, müsst Ihr nur fragen.', `Text0_1_loc3`='Geschaffen von den besten Handwerkern in ganz Teldrassil und als Zeichen der Festigkeit der Allianz zwischen dem Volk des großen Baums und den Menschen dieses Landes durch Boten von Kalimdor nach Azeroth gebracht. Wenn Ihr etwas braucht, müsst Ihr nur fragen.' WHERE (`entry`='1233');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text0_1_loc3`='Seit dem Fall von Dalaran und der Zerstörung von Quel''Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', `Text1_0_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', `Text1_1_loc3`='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE (`entry`='1234');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text0_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text2_0_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', `Text2_1_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE (`entry`='1235');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text0_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text2_0_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', `Text2_1_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE (`entry`='1236');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text0_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_0_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text1_1_loc3`='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', `Text2_0_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', `Text2_1_loc3`='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE (`entry`='1237');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.', `Text0_1_loc3`='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.' WHERE (`entry`='1238');
