UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ab 18 Uhr wird von der höchsten Klippe aus ein herrliches Feuerwerk abgeschossen!', `Text0_1_loc3`='Ab 18 Uhr wird von der höchsten Klippe aus ein herrliches Feuerwerk abgeschossen!', `Text1_0_loc3`='Diese Fässchen sind voll des besten Starkbieres! Trinkt, mein Freund, trinkt und feiert!', `Text1_1_loc3`='Diese Fässchen sind voll des besten Starkbieres! Trinkt, mein Freund, trinkt und feiert!', `Text2_0_loc3`='Ich habe gehört, dass nach dem Feuerwerk Trinkbecher an die Tische gebracht werden. Wenn es soweit ist, lasst uns aufeinander anstoßen!', `Text2_1_loc3`='Ich habe gehört, dass nach dem Feuerwerk Trinkbecher an die Tische gebracht werden. Wenn es soweit ist, lasst uns aufeinander anstoßen!', `Text3_0_loc3`='Grüße, $C! Willkommen zu den Festlichkeiten!', `Text3_1_loc3`='Grüße, $C! Willkommen zu den Festlichkeiten!' WHERE (`entry`='8134');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Ein fröhliches Winterhauchfest, mein Freund!', `Text0_1_loc3`='Ein fröhliches Winterhauchfest, mein Freund!', `Text1_0_loc3`='Frohe Feiertage und Frieden wünsche ich Euch!', `Text1_1_loc3`='Frohe Feiertage und Frieden wünsche ich Euch!', `Text2_0_loc3`='Frohe Feiertage, Freund!', `Text2_1_loc3`='Frohe Feiertage, Freund!', `Text3_0_loc3`='Ah, ein Mistelzweig…', `Text3_1_loc3`='Ah, ein Mistelzweig…' WHERE (`entry`='8139');
UPDATE `locales_npc_text` SET `Text0_1_loc3`='Tolles Wetter hier, oder was meint Ihr?', `Text1_1_loc3`='Warum kann man nie ein Gyrochronatom finden, wenn man mal eins braucht?', `Text2_1_loc3`='Ich werde zuletzt lachen... die werden schon sehen! Oh, hallo $C, habe dich gar nicht bemerkt.', `Text3_1_loc3`='Erfinderei, $g mein Lieber : meine Liebe;, die besteht zu 2% aus in Flaschen abgefüllter Eingebung, zu 4% aus Sonneneinwirkung, zu 6% aus Gewittern, die sich bis zum Nachmittag legen, und zu 93% aus Rumpelkicker.$B$BWartet mal, kommt das insgesamt auf hundert raus? Ich muss hier doch irgendwo meine Notizen rumliegen haben.' WHERE (`entry`='8143');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Urahne Grufhauch verbringt seine Zeit in der Nähe von Brill in Tirisfal.', `Text0_1_loc3`='Urahne Grufhauch verbringt seine Zeit in der Nähe von Brill in Tirisfal.' WHERE (`entry`='8148');
