UPDATE `locales_npc_text` SET `Text0_1_loc3`='Elune sei mit Euch, $C.', `Text1_1_loc3`='Die Hohepriesterin konnte Darnassus nicht verlassen. Fandral hat sehr schlechte Laune.', `Text2_1_loc3`='Tyrande schickt ihre Gebete. Die Mondmutter segnet uns alle.' WHERE (`entry`='8164');
UPDATE `locales_npc_text` SET `Text0_0_loc3`='Urahne Rumpelfels verbringt die Jahre in einer Höhle am Gipfel des Schreckensfelsens in der brennenden Steppe.', `Text0_1_loc3`='Urahne Rumpelfels verbringt die Jahre in einer Höhle am Gipfel des Schreckensfelsens in der brennenden Steppe.' WHERE (`entry`='8168');
